import React, { useEffect, useState } from "react";
import twitterLogo from "./assets/twitter-logo.svg";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import styled from "styled-components";
import idl from "./idl.json";
import { Connection, PublicKey, clusterApiUrl } from "@solana/web3.js";
import { Program, Provider, web3 } from "@project-serum/anchor";
import kp from "./keypair.json";
import { Chip } from "react-rainbow-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowUp, faArrowDown } from "@fortawesome/free-solid-svg-icons";
import CodeEditor from "@uiw/react-textarea-code-editor";
import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";

// Constants
// SystemProgram is a reference to the Solana runtime!
const { SystemProgram } = web3;

// keypair for the account that will hold the notes data.
const arr = Object.values(kp._keypair.secretKey);
const secret = new Uint8Array(arr);
const baseAccount = web3.Keypair.fromSecretKey(secret);

// Get our program's id from the IDL file.
const programID = new PublicKey(idl.metadata.address);

// Set our network to devnet.
const network = clusterApiUrl("devnet");

// Controls how we want to acknowledge when a transaction is "done".
const opts = {
  preflightCommitment: "processed",
};

const TWITTER_HANDLE = "_buildspace";
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;

export const ChipContainer = styled.div`
  position: relative;
  left: 30%;
  top: -12%;
  z-index: 9;
`;

export const UpDownContainer = styled.div`
  position: relative;
  left: -30%;
  top: -7%;
  z-index: 9;
`;

const colours = {
  colors: {
    sandyBrown: "rgba(243,207,79,1)",
    transparentBlack: "rgba(0,0,0,0.25)",
    transparentPaleGoldenrod: "rgba(242,248,175,0.7)",
  },
};

export const PostItNote = styled.div`
  padding: 38.25px 0 0;
`;
export const Note = styled.div`
  width: 500px;
  height: 500px;
  background-color: ${colours.colors.sandyBrown};
  box-shadow: 5px 10px 10px 0 ${colours.colors.transparentBlack};
  position: relative;
  padding: 50px;
`;
export const Tape = styled.div`
  width: 145px;
  height: 39px;
  background-color: ${colours.colors.transparentPaleGoldenrod};
  position: relative;
  left: 30%;
  top: 5%;
  z-index: 9;
`;
export const Editor = styled.div`
  display: block;
  max-height: 260px;
  margin: 0 auto;
  max-width: 593px;
  margin-top: 50px !important;
  overflow: auto;
`;

const App = () => {
  const [walletAddress, setWalletAddress] = useState(null);
  const [inputValue, setInputValue] = useState("");
  const [notesList, setNotesList] = useState([]);
  const [code, setCode] = React.useState(
    `function add(a, b) {\n  return a + b;\n}`
  );

  const checkIfWalletIsConnected = async () => {
    try {
      const { solana } = window;

      if (solana) {
        if (solana.isPhantom) {
          console.log("Phantom wallet found!");
          const response = await solana.connect({ onlyIfTrusted: true });
          console.log(
            "Connected with Public Key:",
            response.publicKey.toString()
          );
          setWalletAddress(response.publicKey.toString());
        }
      } else {
        alert("Solana object not found! Get a Phantom Wallet ðŸ‘»");
      }
    } catch (error) {
      console.error(error);
    }
  };

  const connectWallet = async () => {
    const { solana } = window;

    if (solana) {
      const response = await solana.connect();
      console.log("Connected with Public Key:", response.publicKey.toString());
      setWalletAddress(response.publicKey.toString());
    }
  };

  const getRndInteger = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  };

  const sendNotes = async () => {
    let id = getRndInteger(0, 1000);
    if (inputValue.length === 0) {
      console.log("No note link given!");
      return;
    }
    setInputValue("");
    console.log("Notes link:", inputValue);
    try {
      const provider = getProvider();
      const program = new Program(idl, programID, provider);

      await program.rpc.addNote(inputValue, id, {
        accounts: {
          baseAccount: baseAccount.publicKey,
          user: provider.wallet.publicKey,
        },
      });
      console.log("Note successfully sent to program", inputValue);

      await getNotesList();
    } catch (error) {
      console.log("Error sending Note:", error);
    }
  };

  const upVote = async (id) => {
    try {
      const provider = getProvider();
      const program = new Program(idl, programID, provider);

      await program.rpc.upvoteNote(id, {
        accounts: {
          baseAccount: baseAccount.publicKey,
          user: provider.wallet.publicKey,
        },
      });
      console.log("Vote successfully sent to program", inputValue);

      await getNotesList();
    } catch (error) {
      console.log("Error sending vote:", error);
    }
  };

  const downVote = async (id) => {
    try {
      const provider = getProvider();
      const program = new Program(idl, programID, provider);

      await program.rpc.downvoteNote(id, {
        accounts: {
          baseAccount: baseAccount.publicKey,
          user: provider.wallet.publicKey,
        },
      });
      console.log("Vote successfully sent to program", inputValue);

      await getNotesList();
    } catch (error) {
      console.log("Error sending vote:", error);
    }
  };

  const onInputChange = (event) => {
    const { value } = event.target;
    setInputValue(value);
  };

  const UpDown = (props) => {
    return (
      <div>
        <FontAwesomeIcon icon={faArrowUp} onClick={() => upVote(props.id)} />
        {" " + props.votes + " "}
        <FontAwesomeIcon
          icon={faArrowDown}
          onClick={() => downVote(props.id)}
        />
      </div>
    );
  };

  const PostItNoteComponent = (props) => {
    return (
      <PostItNote>
        <Tape />
        <Note>
          <CodeEditor
            value={props.notes}
            language="js"
            placeholder="Please enter JS code."
            onChange={(evn) => setCode(evn.target.value)}
            padding={15}
            style={{
              fontSize: 12,
              backgroundColor: "#f5f5f5",
              fontFamily:
                "ui-monospace,SFMono-Regular,SF Mono,Consolas,Liberation Mono,Menlo,monospace",
            }}
          />
        </Note>
        <UpDownContainer>
          <UpDown votes={props.votes} id={props.id} />
        </UpDownContainer>
        <ChipContainer>
          <Chip
            className="rainbow-m-around_medium"
            label={"ðŸ : " + props.userAddress.substring(0, 9)}
            variant="neutral"
          />
        </ChipContainer>
      </PostItNote>
    );
  };

  const getProvider = () => {
    const connection = new Connection(network, opts.preflightCommitment);
    const provider = new Provider(
      connection,
      window.solana,
      opts.preflightCommitment
    );
    return provider;
  };

  const createNoteAccount = async () => {
    try {
      const provider = getProvider();
      const program = new Program(idl, programID, provider);
      console.log("ping");
      await program.rpc.startStuffOff({
        accounts: {
          baseAccount: baseAccount.publicKey,
          user: provider.wallet.publicKey,
          systemProgram: SystemProgram.programId,
        },
        signers: [baseAccount],
      });
      console.log(
        "Created a new BaseAccount w/ address:",
        baseAccount.publicKey.toString()
      );
      await getNotesList();
    } catch (error) {
      console.log("Error creating BaseAccount account:", error);
    }
  };

  /*
   * We want to render this UI when the user hasn't connected
   * their wallet to our app yet.
   */
  const renderNotConnectedContainer = () => (
    <button
      className="cta-button connect-wallet-button"
      onClick={connectWallet}
    >
      Connect to Wallet
    </button>
  );

  const renderConnectedContainer = () => {
    // If we hit this, it means the program account hasn't been initialized.
    if (notesList === null) {
      return (
        <div className="connected-container">
          <button
            className="cta-button submit-gif-button"
            onClick={createNoteAccount}
          >
            Do One-Time Initialization For GIF Program Account
          </button>
        </div>
      );
    }
    // Otherwise, we're good! Account exists. User can submit GIFs.
    else {
      return (
        <div className="connected-container">
          <form
            onSubmit={(event) => {
              event.preventDefault();
              sendNotes();
            }}
          >
            <Editor>
              <DropdownButton
                id="dropdown-basic-button"
                title="Dropdown button"
              >
                <Dropdown.Item value="abap">Language: abap</Dropdown.Item>
                <Dropdown.Item value="aes">Language: aes</Dropdown.Item>
                <Dropdown.Item value="apex">Language: apex</Dropdown.Item>
                <Dropdown.Item value="azcli">Language: azcli</Dropdown.Item>
                <Dropdown.Item value="bat">Language: bat</Dropdown.Item>
                <Dropdown.Item value="brainfuck">
                  Language: brainfuck
                </Dropdown.Item>
                <Dropdown.Item value="c">Language: c</Dropdown.Item>
                <Dropdown.Item value="cameligo">
                  Language: cameligo
                </Dropdown.Item>
                <Dropdown.Item value="clike">Language: clike</Dropdown.Item>
                <Dropdown.Item value="clojure">Language: clojure</Dropdown.Item>
                <Dropdown.Item value="coffeescript">
                  Language: coffeescript
                </Dropdown.Item>
                <Dropdown.Item value="cpp">Language: cpp</Dropdown.Item>
                <Dropdown.Item value="csharp">Language: csharp</Dropdown.Item>
                <Dropdown.Item value="csp">Language: csp</Dropdown.Item>
                <Dropdown.Item value="css">Language: css</Dropdown.Item>
                <Dropdown.Item value="dart">Language: dart</Dropdown.Item>
                <Dropdown.Item value="dockerfile">
                  Language: dockerfile
                </Dropdown.Item>
                <Dropdown.Item value="erlang">Language: erlang</Dropdown.Item>
                <Dropdown.Item value="fsharp">Language: fsharp</Dropdown.Item>
                <Dropdown.Item value="go">Language: go</Dropdown.Item>
                <Dropdown.Item value="graphql">Language: graphql</Dropdown.Item>
                <Dropdown.Item value="handlebars">
                  Language: handlebars
                </Dropdown.Item>
                <Dropdown.Item value="hcl">Language: hcl</Dropdown.Item>
                <Dropdown.Item value="html">Language: html</Dropdown.Item>
                <Dropdown.Item value="ini">Language: ini</Dropdown.Item>
                <Dropdown.Item value="java">Language: java</Dropdown.Item>
                <Dropdown.Item value="javascript">
                  Language: javascript
                </Dropdown.Item>
                <Dropdown.Item value="json">Language: json</Dropdown.Item>
                <Dropdown.Item value="jsx">Language: jsx</Dropdown.Item>
                <Dropdown.Item value="julia">Language: julia</Dropdown.Item>
                <Dropdown.Item value="kotlin">Language: kotlin</Dropdown.Item>
                <Dropdown.Item value="less">Language: less</Dropdown.Item>
                <Dropdown.Item value="lex">Language: lex</Dropdown.Item>
                <Dropdown.Item value="livescript">
                  Language: livescript
                </Dropdown.Item>
                <Dropdown.Item value="lua">Language: lua</Dropdown.Item>
                <Dropdown.Item value="markdown">
                  Language: markdown
                </Dropdown.Item>
                <Dropdown.Item value="mips">Language: mips</Dropdown.Item>
                <Dropdown.Item value="msdax">Language: msdax</Dropdown.Item>
                <Dropdown.Item value="mysql">Language: mysql</Dropdown.Item>
                <Dropdown.Item value="nginx">Language: nginx</Dropdown.Item>
                <Dropdown.Item value="objective-c">
                  Language: objective-c
                </Dropdown.Item>
                <Dropdown.Item value="pascal">Language: pascal</Dropdown.Item>
                <Dropdown.Item value="pascaligo">
                  Language: pascaligo
                </Dropdown.Item>
                <Dropdown.Item value="perl">Language: perl</Dropdown.Item>
                <Dropdown.Item value="pgsql">Language: pgsql</Dropdown.Item>
                <Dropdown.Item value="php">Language: php</Dropdown.Item>
                <Dropdown.Item value="plaintext">
                  Language: plaintext
                </Dropdown.Item>
                <Dropdown.Item value="postiats">
                  Language: postiats
                </Dropdown.Item>
                <Dropdown.Item value="powerquery">
                  Language: powerquery
                </Dropdown.Item>
                <Dropdown.Item value="powershell">
                  Language: powershell
                </Dropdown.Item>
                <Dropdown.Item value="pug">Language: pug</Dropdown.Item>
                <Dropdown.Item value="python">Language: python</Dropdown.Item>
                <Dropdown.Item value="r">Language: r</Dropdown.Item>
                <Dropdown.Item value="razor">Language: razor</Dropdown.Item>
                <Dropdown.Item value="redis">Language: redis</Dropdown.Item>
                <Dropdown.Item value="redshift">
                  Language: redshift
                </Dropdown.Item>
